from pyrogram.types import Message
from pyrogram import filters, Client
from config import OWNER_ID
from config import SUDO_USERS
from config import CMD_HANDLER as cmd
from XDB.data import GROUP
from .help import *

@Client.on_message(
    filters.command(["hang"], ".") & (filters.me | filters.user(SUDO_USERS))
)
async def hang(x: Client, message: Message): 
    NOBI = message.text.split(" ")
    if len(NOBI) == 1:
        await message.reply_text(f".𝐡𝐚𝐧𝐠 10")
        return
    if int(message.chat.id)in GROUP:
        await message.reply_text("𝐬𝐫𝐲, 𝐭𝐡𝐢𝐬 𝐠𝐫𝐩 𝐢𝐬 𝐩𝐫𝐨𝐭𝐞𝐜𝐭𝐞𝐝 𝐛𝐲 𝐬𝐨𝐦𝐞 𝐯𝐢𝐬𝐮𝐚𝐥 𝐩𝐨𝐰𝐞𝐫𝐬 🛡️")
